import React, {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios'
const FormWrapper = styled.form `
        background-color: ${props => props.theme.colors.grey_medium};
       max-width: 500px;
       max-height: 300px;
        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(6, 1fr);           
        box-shadow: 2px 2px 2px 2px rgba(35, 35, 35, .5);
        place-content: center;
        justify-content: center;
        align-items: center;
        `
const Label = styled.label `
        padding: 0 15px;
        grid-column: 2/3;
        font-size: 1.2rem;
        line-height: 1.2rem;
        color: ${props => props.theme.colors.midnight};
        `
const Option = styled.option `
        margin: 0 15px;
        //grid-column: 2/3;
        font-size: 1rem;
        line-height: 1rem;
        background-color: ${props => props.theme.colors.white};
    color:  ${props => props.theme.colors.select};
        `
const Header = styled.h1 `
    grid-column: 2/6;
    color: ${props => props.theme.colors.midnight};
    text-align: center;
    `
const Select = styled.select `
   background-color: ${props => props.theme.colors.white};
    color:  ${props => props.theme.colors.select};
    outline-color: ${props => props.theme.colors.blue_light};

    `
const Subheader = styled.h3 `
    grid-column: ${props => props.column};
    grid-row: ${props => props.row};
    `
const Input = styled.input `
    padding: 3px;
    grid-column: 3/6;
    outline-color: ${props => props.theme.colors.blue_light};
    color: ${props => props.theme.colors.midnight};
    background-color: ${props => props.theme.colors.white};
    margin:5px;
    padding: 5px;

    `
const Row = styled.div `
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    grid-column: 1/7; 
    grid-row: ${props => props.row};
    `
const Button = styled.button `
    padding: 3px;
    font-size: 1.2rem;
    background-color: ${props => props.theme.colors.midnight};
    color:  ${props => props.theme.colors.white};
    grid-column: 3/5;
    font-weight: 500;
    margin:5px;
    outline-color: ${props => props.theme.colors.blue_light};
    `
const Radio = styled.input `
   margin: 0 10px;
   `
const TextArea = styled.textarea `
    grid-column: 3/6;
    margin: 5px;
    resize: none;
    padding: 5px;
    font-size: .75rem;
    line-height: 1rem;
    background-color: ${props => props.theme.colors.white};
    color:  ${props => props.theme.colors.midnight};
    outline-color: ${props => props.theme.colors.blue_light};
    box-shadow: inset 2px 2px 0px ${props => props.theme.colors.midnight}, 2px 2px 0 ${props => props.theme.colors.grey_dark};
    `
function NewChoreForm(props) {
  const [state,
    setState] = useState({name: "", details: "", pointValue: 0, frequency: "", minage: 0})

    const handleChange = (e) => {
    const {name, value} = e.target
    setState(prev => ({
      ...prev,
      [name]: value
    }))
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    const newChore = {...state, user: null, available: true}
    axios
      .post("/chores", state)
      .then(resp => {
        console.log("chore added")
        setState({name: "", details: "", pointValue: 0, frequency: "weekly", minage: 0})
      })
      .catch(err => console.log(err))
  }

  return (
    <FormWrapper onSubmit={handleSubmit}>
      <Row>
        <Header>
          Add new chore
        </Header>
      </Row>
      <Label htmlFor="name">Title
      </Label>
      <Input id="name" name="name" value={state.name} onChange={handleChange}/>
      <Label htmlFor="details">Description</Label>
      <TextArea
        id="details"
        name="details"
        value={state.details}
        onChange={handleChange}
        rows="3"/>
      <Label>Frequency</Label>
      <Select value={state.frequency} onChange={handleChange} name="frequency">
        <Option value="">
          Select One
        </Option>
        <Option value="daily">
          Daily
        </Option>
        <Option value="weekly">Weekly

        </Option>
        <Option value="asneeded">As Needed
        </Option>
      </Select>
      <Label htmlFor="pointvalue">Point Value</Label>
      <Input
        min="0"
        id="pointvalue"
        type="number"
        name="pointValue"
        value={state.pointValue}
        onChange={handleChange}/>
      <Label htmlFor="minage">Minimum Age</Label>
      <Input
        type="number"
        name="minage"
        id="minage"
        value={state.minage}
        onChange={handleChange}/>
      <Button type="submit">Add</Button>
    </FormWrapper>
  )
}

export default NewChoreForm
