import React from 'react'
import styled from 'styled-components'
import {NavLink} from 'react-router-dom'

function NavBar(props) {
    const Nav = styled.nav `

    width: 100%;
    position: absolute;
    height: 3rem;
    display: flex;
    justify-content: flex-end;
    padding: 0;
    align-items: center;
    background-color: ${props=>props.theme.colors.asphalt};
    `
  const StyledNavLink = styled(NavLink)`
    padding: 0 35px;
    margin: 0;
    font-size: 1.2rem;
    line-height: 1.2rem;
    //place-content: center;
    display:inline-flex;
    justify-content: center;
    align-items: center;
    height:100%;
    color: #ecf0f1;
`
  return (
      <Nav>
          <StyledNavLink  activeStyle={{
          background: "#ecf0f1",
          color: "#232323"
        }} exact to="/">Home</StyledNavLink>
         <StyledNavLink  activeStyle={{
          background: "#ecf0f1",
          color: "#232323"
        }} to="/profile">Profile</StyledNavLink>
          <StyledNavLink  activeStyle={{
          background: "#ecf0f1",
          color: "#232323"
        }} to="/settings">Settings</StyledNavLink>
      </Nav>
  )
}

export default NavBar
