import React, {useState, useEffect} from 'react'
import axios from 'axios'

const ChoreContext = React.createContext(null)

const ChoreContextProvider = ({children}) => {
  const [chores,
    setChores] = useState([])
  const updateChores = ()=>{
    axios
      .get("/chores")
      .then(resp => {
          console.log(resp)
        setChores(resp.data)
      })
      .catch(err => console.log(err))
  }
  useEffect(() => {
    updateChores
  }, [])
  const assignChore = (choreId, userId)=>{
      axios.put(`/chores/updateOne/${choreId}`, {user: userId, available: false}).then(resp=>{
        console.log(resp)
        updateUsers()
      }).catch(err=>console.log(err))
  }

  const unAssignChore = (choreId)=>{
    axios.put(`chores/updateOne/${choreId}`, {user: null, completed: false, available: true})
    .then(res=>{
     updateChores()
    }).catch(err=>console.log(err))
  }

  const resetChores= (weekly=true, monthly=true, asneeded=true)=>{
      axios.get(`/resetAll/?weekly=${weekly}&monthly=${monthly}&asneeded=${asneeded}`).then((res)=>{
        updateChores()
      }).catch(err=>console.log(err))
  }

  return (
    <ChoreContext.Provider value={{
      chores,
      setChores, 
      assignChore
    }}>
      {children}
    </ChoreContext.Provider>
  )
}

export {ChoreContext, ChoreContextProvider}
