import React, {useState} from 'react'
import styled from 'styled-components'

function SignInForm(props){
    const FormWrapper = styled.form`
        background-color: ${props=>props.theme.colors.grey_medium};
        grid-column: 3/11;

    `
    const Label = styled.label`

    `
    const Header = styled.h1`
        color: ${props=>props.theme.colors.red};
    `
    const Input = styled.input`

    `
    const Row = styled.div`
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;
    `
    const Button = styled.button``
    const [state, setState] = useState({
        name: "",
        email: ""
    })
    const handleChange = (e)=>{
        const {name, value} = e.target
        setState(prev=>({
            ...prev,
            [name]: value
        }))
    }
    const handleSubmit = (e)=>{
        e.preventDefault()
        console.log(state.name)
        setState({
            name: "",
            email:""
        })
    }
    return (
        <FormWrapper onSubmit={handleSubmit}>
            <Row>
                <Header>
                    Sign in
                </Header>
            </Row>
            <Row>
                <Label htmlFor="name">Name</Label>
                <Input id="name" onChange={handleChange} placeholder="First Last" name="name" value={state.name}/>
            </Row>
            <Row>
                <Label htmlFor="email">email</Label>
                <Input id="email" placeholder="example@gmail.com" name="email" value={state.email} onChange={handleChange} />
            </Row>
            <Row>
                <Button type="Submit">Sign In</Button>
            </Row>
        </FormWrapper>
    )
}

export default SignInForm
