import React, {useContext, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {UserContext} from '../context/UserContext'
import {ChoreContext} from '../context/ChoreContext'

const CardWrapper = styled.div `
      display: flex;
    flex-direction: column;
    border: thick solid ${props => props.theme.colors.purple_light};
    box-shadow: inset 2px 2px 2px 0 rgba(255,255,255,.7), 2px 2px 2px 0 rgba(0,0,0,.7);
    border-radius: 10px;
    width: max-content;
    height: max-content;
    grid-column: ${props => props.column};
    grid-row: ${props => props.row};
    padding: 10px;
    margin: 20px;
    background-color: ${props=>props.theme.colors.purple};
    color: ${props=>props.theme.colors.grey_light};
`
const Button = styled.button``
const Header = styled.h3 `
`
const Paragraph = styled.p `
transition: 2s ease all;`
const Chore = props => {
    const {users} = useContext(UserContext)
    const {assignChore} = useContext(ChoreContext)
    const handleClick = (e)=>{
        assignChore(props._id, users.current._id)
    }
    const [expanded, setExpanded] = useState(false)
  return (
    <CardWrapper>
      <Header onClick={handleClick}>{props.name}</Header>
      <Paragraph>{props.frequency}</Paragraph>
      <Paragraph>{props.pointValue} Points</Paragraph>
      {props.minage ?
      <Paragraph>{props.minage}</Paragraph> : null}
      { expanded ? 
      <Paragraph>{props.details}</Paragraph> : null}
      <Button onClick={(e)=>{
          e.preventDefault()
          setExpanded(prev=>!prev)
      }}>{expanded ? "less" : "more"}</Button>
    </CardWrapper>

  )
}

export default Chore
