import React, {useContext, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {UserContext} from '../context/UserContext'
import {ChoreContext} from '../context/ChoreContext'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faWindowClose, faPlusCircle, faMinusCircle, faCaretDown, faCaretUp} from '@fortawesome/free-solid-svg-icons'

const CardWrapper = styled.div `
    display: flex;
    flex-direction: column;
    border-radius: 5px;
    width: auto;
    height: auto;
    padding: 20px;
    margin: 10px;
    background-color: ${props => props.theme.light};
    color: ${props => props.theme.colors.dark};
    position: relative;

`
const Close = styled(FontAwesomeIcon)`

  color: ${props=>props.theme.colors.red};
  position: absolute;
  top: 10px;
  right: 10px;
  font-size: 24px;
  text-shadow: 2px 2px 5px #000;
  transition: .2s ease all;
  cursor: pointer;
  &:hover{
    color: ${props=>props.theme.colors.red_light};
  }
`
const Add = styled(FontAwesomeIcon)`
  font-size: 32px;
  color: ${props=>props.theme.colors.green};
  position: absolute;
  top: 10px;
  right: 10px;
  transition: .2s ease all;
  cursor: pointer;
  &:hover{
    color: ${props=>props.theme.colors.green_light};
  }
`
const Toggle = styled(FontAwesomeIcon)`
font-size: 24px;
color: ${props=>props.theme.blue};
`
const Row = styled.div `
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
`
const Button = styled.button `

`
const Checkbox = styled.input`

`
const Header = styled.h1 `
    text-transform: capitalize;
`
const SubHeader = styled.h3 `
    text-transform: capitalize;
    margin: 0 25px;
`
const Paragraph = styled.p `
    transition: 2s ease all;
`
const Label = styled.label`
  color: ${props=>props.theme.highlight};
`

const Chore = props => {
  const [completed, setCompleted] = useState(false)
  const {users} = useContext(UserContext)
  const {assignChore, unAssignChore, markComplete, updateChores} = useContext(ChoreContext)
  const handleClick = (e) => {
    assignChore(props._id, users.current._id)
  }
  const [expanded,
    setExpanded] = useState(false)
  const toggleExpanded = (e) => {
    e.preventDefault()
    setExpanded(prev => !prev)
  }
  const handleCheckbox = (e)=>{
    const prom = markComplete(props._id)
    updateChores()
    prom.then(resp=>setCompleted(true)).catch(err=>console.log(err))

  }
  return (
    <CardWrapper>
        {props.user === null ? <Add icon={faPlusCircle} onClick={(e)=>assignChore(props._id, users.current._id)}/> : <Close icon={faWindowClose} onClick={(e)=>unAssignChore(props._id)} />}
      <Row>
        <Header>{props.name}</Header>
        <SubHeader>{props.frequency}</SubHeader>
        <SubHeader>{`${props.pointValue} Points`}</SubHeader>
        {props.minage
          ? <SubHeader>{`Ages ${props.minage} and up`}</SubHeader>
          : null}
        </Row>
        <Row><Label>Done <Checkbox onChange={handleCheckbox} type="checkbox" name="done" checked={completed}/></Label></Row>

      <Row>
        {expanded
          ? <Paragraph>{props.details}</Paragraph>
          : null}
      </Row>
      <Toggle onClick={toggleExpanded} icon={expanded ? faCaretUp : faCaretDown}/>

    </CardWrapper>

  )
}

export default Chore
