import React from 'react'
import styled, {ThemeProvider} from 'styled-components'
import {Route, Link, Switch, useHistory} from 'react-router-dom'
import NavBar from './components/NavBar'
import Home from './pages/Home'
import Profile from './pages/Profile'
import Settings from './pages/Settings'
import {UserContextProvider} from './context/UserContext'
import {ChoreContextProvider} from './context/ChoreContext'

const AppWrapper = styled.div `
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    width: 100%;
    height: 100vh;
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows:  1fr auto 1fr;
  background: ${props=>props.theme.colors.blue}
  linear-gradient(135deg, ${props=>props.theme.colors.red_light} 25%, transparent 25%) -50px 0,
  linear-gradient(225deg, ${props=>props.theme.colors.red_light} 25%, transparent 25%) -50px 0,
  linear-gradient(315deg, ${props=>props.theme.colors.red_light} 25%, transparent 25%),
  linear-gradient(45deg, ${props=>props.theme.colors.red_light} 25%, transparent 25%);	
  background-size: 2em 2em;
  background-color:  ${props=>props.theme.colors.red} ;

`
const Content = styled.main `
    width: 100%;
    height: 100%;
    grid-column: 1/2;
    grid-row: 2/3;
`
function App() {
  return (
    <ChoreContextProvider>
    <UserContextProvider>
      <AppWrapper>
        <NavBar/>
        <Content>
          <Switch>
            <Route exact path="/">
              <Home/>
            </Route>
            <Route path="/profile">
              <Profile/>
            </Route>
            <Route path="/settings">
              <Settings/>
            </Route>
          </Switch>
        </Content>
      </AppWrapper>
    </UserContextProvider>
    </ChoreContextProvider>
  )
}

export default App
