import React, {useContext} from 'react'
import styled, {withTheme} from 'styled-components'
import {UserContext} from '../context/UserContext'
const CardWrapper = styled.div `
    display: flex;
    flex-direction: column;
    border: thin solid ${props => props.theme.colors.midnight};
    border-radius: 10px;
    width: 100%;
    height: 100%;
    grid-column: ${props => props.column};
    grid-row: ${props => props.row};
    padding: 10px;
    margin: 10px;
`
const Header = styled.h3 `
`
const Paragraph = styled.p ``
const UserCard = props => {
    const {users, setUser} = useContext(UserContext)
    const handleClick=(e)=>{
        e.preventDefault()
        const currentUser = users.all.find(user=> user._id === props._id)
        setUsers(prev=>({
            ...prev, 
            current: currentUser
        }))
    }
  return (
    <CardWrapper>
      <Header>{props.userName}</Header>
      <Paragraph>Chores: No Chores</Paragraph>
      <Paragraph>Points: {props.points}</Paragraph>
    </CardWrapper>

  )
}

export default UserCard
