import React, {useContext, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {UserContext} from '../context/UserContext'
import {ChoreContext} from '../context/ChoreContext'
import {CaretDownFill, CaretUpFill} from 'grommet-icons'

const CardWrapper = styled.div `
    display: flex;
    flex-direction: column;
    box-shadow: inset 1px 1px 1px rgba(255,255,255,.7), 1px 1px 1px rgba(0,0,0,.7);
    border-radius: 5px;
    width: auto;
    height: auto;
    padding: 15px;
    margin: 20px;
    background-color: ${props => props.theme.colors.purple};
    color: ${props => props.theme.colors.grey_light};
`
const Row = styled.div `
    display: flex;
    flex-direction: row;
`
const Button = styled.button ``
const Header = styled.h1 `
    text-transform: capitalize;
`
const Paragraph = styled.p `
    transition: 2s ease all;
`

const Chore = props => {
  const {users} = useContext(UserContext)
  const {assignChore} = useContext(ChoreContext)
  const handleClick = (e) => {
    assignChore(props._id, users.current._id)
  }
  const [expanded,
    setExpanded] = useState(false)
  const toggleExpanded = (e) => {
    e.preventDefault()
    setExpanded(prev => !prev)
  }
  return (
    <CardWrapper>
      <Row>
        <Header onClick={handleClick}>{props.name}</Header>
      </Row>
      <Row>
        <Paragraph>{props.frequency}</Paragraph>
        <Paragraph>{props.pointValue}
          Points</Paragraph>
        {props.minage
          ? <Paragraph>{props.minage}</Paragraph>
          : null}
      </Row>
      <Row>
        {expanded
          ? <Paragraph>{props.details}</Paragraph>
          : null}
      </Row>
      <Button onClick={toggleExpanded}>{expanded
          ? "less"
          : "more"
}</Button>
    </CardWrapper>

  )
}

export default Chore
