import React, {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios'
const FormWrapper = styled.form `
        background-color: ${props => props.theme.colors.grey_medium};
        width: max-content;

        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(6, 1fr);           
        box-shadow: 2px 2px 2px 2px rgba(35, 35, 35, .5);
        place-content: center;
        justify-content: center;
        align-items: center;
        `
const Label = styled.label `
        padding: 0 15px;
        grid-column: 2/3;
        font-size: 1.2rem;
        line-height: 1.2rem;
        color: ${props => props.theme.colors.midnight};
        `
const Option = styled.label `
        margin: 0 15px;
        //grid-column: 2/3;
        font-size: 1rem;
        line-height: 1rem;
        color: ${props => props.theme.colors.midnight};
        `
const Header = styled.h1 `
    grid-column: 2/6;
    color: ${props => props.theme.colors.midnight};
    text-align: center;
    `
const Subheader = styled.h3 `
    grid-column: ${props => props.column};
    grid-row: ${props => props.row};
    `
const Input = styled.input `
        padding: 3px;
        grid-column: 3/6;
        outline-color: ${props => props.theme.colors.midnight};
        color: ${props => props.theme.colors.midnight};
        background-color: ${props => props.theme.colors.white};
        margin:5px;

        `
const Row = styled.div `
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;
        grid-column: 1/7; 
        grid-row: ${props => props.row};
        `
const Button = styled.button `
    padding: 3px;
    font-size: 1.2rem;
    background-color: ${props => props.theme.colors.midnight};
    color:  ${props => props.theme.colors.white};
    grid-column: 3/5;
    font-weight: 500;
    margin:5px;
    `
const Radio = styled.input `

   margin: 0 10px;
   `

const TextArea = styled.textarea `
    grid-column: 3/6;
    grid-row: 3/6;
    margin: 5px;
    resize: none;
    `
function NewUserForm(props) {
  const [state,
    setState] = useState({userName: "", age: 0})

  const handleChange = (e) => {
    const {name, value} = e.target
    setState(prev => ({
      ...prev,
      [name]: value
    }))
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    axios
      .post("/users", state)
      .then(resp => {
        console.log("user added")
        setState({userName: "", age: null})
      })
      .catch(err => console.log(`${state.userName} user in use`))
  }

  return (
    <FormWrapper onSubmit={handleSubmit}>
      <Header>Add New User</Header>
      <Header></Header>
      <Label htmlFor="userName">Name</Label>
      <Input
        name="userName"
        id="userName"
        value={state.userName}
        onChange={handleChange}/>
      <Label htmlFor="age">Age</Label>
      <Input type="number" name="age" id="age" value={state.age} onChange={handleChange}/>
      <Button type="submit">Submit</Button>
    </FormWrapper>
  )
}

export default NewUserForm
