import React, {useContext} from 'react'
import styled from 'styled-components'
import {UserContext} from '../context/UserContext'
import {ChoreContext} from '../context/ChoreContext'
import ChoreCard from './ChoreCard'

const Wrapper = styled.div `
display: grid;
grid-template-columns: 1fr 1fr;
width: max-content;
`
const Header = styled.h1`
text-align: center;
`
const Paragraph = styled.p ``
const UserChores = (props) =>{
    const {users} = useContext(UserContext)
    const {chores} = useContext(ChoreContext)
    const usersChores = chores.filter((chore)=>chore.user = users.current._id)

    return (
        <Wrapper>
            <Header>
                {users.current.userName}'s Chores
            </Header>
            {usersChores.length ? usersChores.map(chore=><ChoreCard key={chore._id} {...chore}/>) : <Paragraph>{users.current.userName} has no current chores assigned.</Paragraph> }
        </Wrapper>
    )
}

export default UserChores
