import React, {useContext, useEffect, useState} from 'react'
import styled, { withTheme } from 'styled-components'
import {UserContext} from '../context/userContext'
import UserCard from './UserCard'
const Wrapper = styled.div`
display: grid;
grid-template-columns: 1fr 1fr;
`
const UserPicker = props=>{
    const {users} = useContext(UserContext)
    const userCards = users.all.map((user, indx)=><UserCard username={user.username} currentChore={user.currentChore} points={user.point} column={indx%2==0 ? 2/3 : 1/2} row={Math.floor(indx/2) / Math.ceil(indx/2)}/>)
    return (
        <Wrapper>
            {userCards}
        </Wrapper>
    )
}
