import React, {useContext, useEffect, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {ChoreContext} from '../context/ChoreContext'
import ChoreCard from './ChoreCard'
const Wrapper = styled.div `
display: grid;
width: 100%;
@media (max-width: 1000) {
grid-template-columns: 1fr 1fr 1fr 1fr;
}
@media (max-width: 768) {
grid-template-columns: 1fr 1fr;
}`
const Header = styled.h1 `
@media (max-width: 480) {
grid-template-columns: 1fr;
}
grid-column: 1/3;
`
const Row = styled.div`
grid-column: 1/3;
display: flex;
place-content: center;
`

const ChorePicker = props => {
  let availableChores
  const [available, setAvailable] = useState([])
  const {chores, setChores} = useContext(ChoreContext)
  useEffect(() => {
    availableChores = chores.filter(chore=>chore.user === null)
    setAvailable(availableChores)
  }, [chores])

  const choreCards = available
    .map((chore) =>< ChoreCard 
    key={chore._id}
    {...chore}
    />)
  return (
    <Wrapper>
      <Header>Available Chores</Header>
      {choreCards}
    </Wrapper>
  )
}

export default ChorePicker
