import React, {useState} from 'react'
import styled from 'styled-components'

function NewChoreForm(props) {
  const FormWrapper = styled.form `
        background-color: ${props => props.theme.colors.grey_medium};
        grid-column: ${props=>props.column};
        grid-row: ${props=>props.row};
        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(6, 1fr);           
        box-shadow: 2px 2px 2px 2px rgba(35, 35, 35, .5);
        place-content: center;
    `
  const Label = styled.label `
        padding: 15px 0;
        grid-column: ${props=> props.column};
        font-size: 1.2rem;
        line-height: 1.2rem;
        color: ${props => props.theme.colors.midnight};
     `
  const Header = styled.h1 `
    grid-column: 2/6;
        color: ${props => props.theme.colors.midnight};
        text-align: center;
    `
  const Subheader = styled.h3 `
    grid-column: ${props=>props.column};
    grid-row: ${props=>props.row};
    `
  const Input = styled.input `
        padding: 3px;
        grid-column: 3/6;
        outline-color: ${props => props.theme.colors.midnight};
        color: ${props => props.theme.colors.midnight};
        background-color: ${props => props.theme.colors.white};
        margin:5px;

    `
  const Row = styled.div `
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;
        grid-column: 1/7; 
        grid-row: ${props => props.row};
    `
  const Button = styled.button `
    padding: 3px;
    font-size: 1.2rem;
    background-color: ${props => props.theme.colors.midnight};
    color:  ${props => props.theme.colors.white};
    grid-column: 3/5;
    font-weight: 500;
    margin:5px;
  `
  const Radio = styled.input ``

  const TextArea = styled.textarea `
    grid-column: ${props=props.column};
  `

  const initState = {
    name: "",
    details: "",
    pointvalue: 0,
    frequency: "",
    minage: null
  }
  const [state,
    setState] = useState(initState)

  const handleChange = (e) => {
    const {name, value} = e.target
    setState(prev => ({
      ...prev,
      [name]: value
    }))
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(state.name)
    setState({name: "", email: ""})
  }

  return (
    <FormWrapper>
        <Row>
            <Header>
                Add new chore
            </Header>
        </Row>
      <Label htmlFor="name">Title </Label>
        <Input id="name" name="name" value={state.name} onChange={handleChange}/>
      <Label htmlFor="description">Description</Label>
        <TextArea
            column="2/5"
            id="descriprion"
            name="description"
            value={state.description}
            onChange={handleChange}/>
        <Subheader>Frequency</Subheader>
      <Row>
        <Label>Daily
          <Radio
            type="radio"
            name="frequency"
            value="daily"
            checked={state.frequency === "daily"}
            onChange={handleChange}/>
        </Label>
        <Label>Weekly
          <Radio
            type="radio"
            name="frequency"
            value="weekly"
            checked={state.frequency === "weekly"}
            onChange={handleChange}/>
        </Label>
        <Label>As Needed
          <Radio
            type="radio"
            name="frequency"
            value="asneeded"
            checked={state.frequency === "asneeded"}
            onChange={handleChange}/>
        </Label>
        `
      </Row>
    </FormWrapper>
  )
}

export default NewChoreForm
