import React, {useContext, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {UserContext} from '../context/UserContext'
import {ChoreContext} from '../context/ChoreContext'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faWindowClose, faPlusCircle, faMinusCircle, faCaretDownFill, faCaretUpFill} from '@fortawesome/free-solid-svg-icons'

const CardWrapper = styled.div `
    display: flex;
    flex-direction: column;
    border-radius: 5px;
    width: auto;
    height: auto;
    padding: 20px;
    margin: 10px;
    background-color: ${props => props.theme.colors.white};
    color: ${props => props.theme.colors.black};
    position: relative;
`
const Close = styled(FontAwesomeIcon)`
  width: 32px;
  height: 32px;
  color: ${props=>props.theme.colors.red};
  position: absolute;
  top: 10px;
  right: 10px;
`
const Add = styled(FontAwesomeIcon)`
  width: 32px;
  height: 32px;
  color: ${props=>props.theme.colors.green};
  position: absolute;
  top: 10px;
  right: 10px;
`
const Row = styled.div `
    display: flex;
    flex-direction: row;
`
const Button = styled.button `

`
const Header = styled.h1 `
    text-transform: capitalize;
`
const Paragraph = styled.p `
    transition: 2s ease all;
`

const Chore = props => {
  const {users} = useContext(UserContext)
  const {assignChore} = useContext(ChoreContext)
  const handleClick = (e) => {
    assignChore(props._id, users.current._id)
  }
  const [expanded,
    setExpanded] = useState(false)
  const toggleExpanded = (e) => {
    e.preventDefault()
    setExpanded(prev => !prev)
  }
  return (
    <CardWrapper>
      <Row>
        <Header>{props.name}</Header>
        {props.user === null ? <Close icon={faWindowClose}/> : <Add icon={faPlusCircle}/>}
        </Row>
      <Row>
        <Paragraph>{props.frequency}</Paragraph>
        <Paragraph>{props.pointValue}
          Points</Paragraph>
        {props.minage
          ? <Paragraph>{props.minage}</Paragraph>
          : null}
      </Row>
      <Row>
        {expanded
          ? <Paragraph>{props.details}</Paragraph>
          : null}
      </Row>
      <Toggle icon={props.user === null ? faPlusCircle : faMinusCircle} />
      <Button onClick={toggleExpanded}>{expanded
          ? "less"
          : "more"
}</Button>
    </CardWrapper>

  )
}

export default Chore
