import React, {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios'
const FormWrapper = styled.form `
        background-color: ${props => props.theme.colors.grey_medium};
        width: max-content;
        grid-row: 2/3;
        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(6, 1fr);           
        box-shadow: 2px 2px 2px 2px rgba(35, 35, 35, .5);
        place-content: center;
        justify-content: center;
        align-items: center;
        `
const Label = styled.label `
        padding: 0 15px;
        grid-column: 2/3;
        font-size: 1.2rem;
        line-height: 1.2rem;
        color: ${props => props.theme.colors.midnight};
        `
const Option = styled.label `
        margin: 0 15px;
        //grid-column: 2/3;
        font-size: 1rem;
        line-height: 1rem;
        color: ${props => props.theme.colors.midnight};
        `
const Header = styled.h1 `
    grid-column: 2/6;
    color: ${props => props.theme.colors.midnight};
    text-align: center;
    `
const Subheader = styled.h3 `
    grid-column: ${props => props.column};
    grid-row: ${props => props.row};
    `
const Input = styled.input `
    padding: 3px;
    grid-column: 3/6;
    outline-color: ${props => props.theme.colors.midnight};
    color: ${props => props.theme.colors.midnight};
    background-color: ${props => props.theme.colors.white};
    margin:5px;

    `
const Row = styled.div `
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    grid-column: 1/7; 
    grid-row: ${props => props.row};
    `
const Button = styled.button `
    padding: 3px;
    font-size: 1.2rem;
    background-color: ${props => props.theme.colors.midnight};
    color:  ${props => props.theme.colors.white};
    grid-column: 3/5;
    font-weight: 500;
    margin:5px;
    `
const Radio = styled.input `
   margin: 0 10px;
   `
const TextArea = styled.textarea `
    grid-column: 3/6;
    margin: 5px;
    resize: none;
    `
function NewChoreForm(props) {
  const [state,
    setState] = useState({name: "", details: "", pointValue: 0, frequency: "", minage: null})
  const handleChange = (e) => {
    const {name, value} = e.target
    setState(prev => ({
      ...prev,
      [name]: value
    }))
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    axios
      .post("/chores", state)
      .then(resp => {
        console.log("chore added")
        setState({name: "", details: "", pointValue: 0, frequency: "", minage: null})
      })
      .catch(err => console.log(err))
  }

  return (
    <FormWrapper onSubmit={handleSubmit}>
      <Row>
        <Header>
          Add new chore
        </Header>
      </Row>
      <Label htmlFor="name">Title
      </Label>
      <Input id="name" name="name" value={state.name} onChange={handleChange}/>
      <Label htmlFor="details">Description</Label>
      <TextArea
        id="details"
        name="details"
        value={state.description}
        onChange={handleChange}/>
      <Label>Frequency</Label>
      <Option>Daily
        <Radio
          type="radio"
          name="frequency"
          value="daily"
          checked={state.frequency === "daily"}
          onChange={handleChange}/>
      </Option>
      <Option>Weekly
        <Radio
          type="radio"
          name="frequency"
          value="weekly"
          checked={state.frequency === "weekly"}
          onChange={handleChange}/>
      </Option>
      <Option>As Needed
        <Radio
          type="radio"
          name="frequency"
          value="asneeded"
          checked={state.frequency === "asneeded"}
          onChange={handleChange}/>
      </Option>
      <Label>Point Value</Label>
      <Input
        type="number"
        name="pointValue"
        value={state.pointvalue}
        onChange={handleChange}/>
      <Button type="submit">Add</Button>
    </FormWrapper>
  )
}

export default NewChoreForm
