import React, {useContext, useEffect, useState} from 'react'
import styled, {withTheme} from 'styled-components'
import {ChoreContext} from '../context/ChoreContext'
import ChoreCard from './ChoreCard'
const Wrapper = styled.div `
display: grid;
grid-template-columns: 1fr 1fr;
width: max-content;
`
const Header = styled.h3 `
grid-column: 1/3;
`
const Row = styled.div`
grid-column: 1/3;
display: flex;
place-content: center;
`

const ChorePicker = props => {
  const {chores, setChores} = useContext(ChoreContext)
  const availableChores = chores.filter(chore=>chore.user === null)
  const choreCards = availableChores
    .map((chore) =>< ChoreCard 
    key={chore._id}
    name = {
      chore.name
    }
    details={chore.details}
    frequency = {
      chore.frequency
    }
    pointValue = {
      chore.pointValue
    } 
    _id = {chore._id} minage={chore.minage}
    />)
  return (
    <Wrapper>
      <Header>Available Chores</Header>
      {choreCards}
    </Wrapper>
  )
}

export default ChorePicker
