import React from 'react'
import styled from 'styled-components'
import {NavLink} from 'react-router-dom'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faHome, faTasks, faUserCog} from '@fortawesome/free-solid-svg-icons'
const Nav = styled.nav `
    grid-column: 1/2;
    grid-row: 1/2;
    width: 100%;
    //: absolute;
    height: 3rem;
    display: flex;
    justify-content: flex-end;
    padding: 0;
    align-items: center;
    background-color: ${props => props.theme.colors.asphalt};
    `
    const Button = styled.button `
    padding: 3px;
    font-size: 1.2rem;
    background-color: ${props => props.theme.colors.midnight};
    color:  ${props => props.theme.colors.white};
    grid-column: 3/5;
    font-weight: 500;
    margin:5px;
    outline-color: ${props => props.theme.colors.blue_light};
    `
const StyledNavLink = styled(NavLink)`
    padding: 0 35px;
    margin: 0;
    font-size: 1.5rem;
    line-height: 1.2rem;
    //place-content: center;
    display:inline-flex;
    justify-content: center;
    align-items: center;
    height:100%;
    color: ${props => props.theme.colors.white};
    transition: all 100ms ease;
    `
function NavBar(props) {
  return (
    <Nav>
      <StyledNavLink
        activeStyle={{
        background: "#ecf0f1",
        color: "#232323"
      }}
        exact
        to="/"><FontAwesomeIcon icon={faHome}/></StyledNavLink>
      <StyledNavLink
        activeStyle={{
        background: "#ecf0f1",
        color: "#232323"
      }}
        to="/profile"><FontAwesomeIcon icon={faTasks}/></StyledNavLink>
      <StyledNavLink
        activeStyle={{
        background: "#ecf0f1",
        color: "#232323"
      }}
        to="/settings"><FontAwesomeIcon icon={faUserCog}/></StyledNavLink>
      <Button as="StyledLink"><FontAwesomeIcon icon={faUserCog}/></Button>
    </Nav>
  )
}

export default NavBar
